import React, { Component } from "react";
import GameHeader from "./GameHeader";
import GameShow from "./GameShow";
import GameFooter from "./GameFooter";
import UserBar from "./UserBar";

let baseURL = "http://localhost:3000/";
class App extends Component {
  constructor() {
    super();
    this.state = {
      level: [],
      monsters: [],
      users: [],
      activeUser: ["user"],
      words: [],
      bossWords: [],
      activeWords: ["walter", "fish"],
    };
  }
  componentDidMount() {
    this.fetchLevel();
    this.fetchUsers();
    this.fetchMonsters()

  }





  //****FETCH FUNCTIONS THAT FOR SOME REASON I COULDN'T REFACTOR?? REACT IS DUMB****//
  fetchLevel = () => {
    return fetch(baseURL + "levels")
      .then((resp) => resp.json())
      .then((level) => this.setState({ level }));
  };
  fetchUsers = () => {
    return fetch(baseURL + "users")
      .then((resp) => resp.json())
      .then((users) => this.setState({ users }));
  };
  fetchMonsters = () => {
    return fetch(baseURL + "monsters")
      .then((resp) => resp.json())
      .then((monsters) => this.setState({ monsters }));
  };






  //***Helper functions *//
  checkUserLogin = (event) => {
    const index = this.state.users.indexOf(event.target.value);
    if (index > -1) {
      console.log(event.target.value);
      this.setState({ value: "" });
      event.target.value = "";
    }
  };

  checkLogIn = () => {
    if (this.state.activeUser.length > 0) {
      return <GameShow words={this.state.level} />;
    } else {
      return <UserBar logIn={this.checkUserLogin}/>;
    }
  };

  render() {
    return (
      <div className="App">
        <GameHeader />
        {/* <GameShow words={this.state.level}/> */}
        {this.checkLogIn()}
        <GameFooter words={this.state.activeWords} />
      </div>
    );
  }
}
export default App;
