import React, { Component } from 'react';
import HeroSprite from './sprites/hero.png'

class Hero extends Component {
    constructor(){
        super();
        this.state = {
            hero: null,
        }
    }

    componentDidMount() {
        const hero = new Image()
        hero.src = HeroSprite
        this.setState({ hero: hero })
    }


    renderHero = () => {
        this.state.hero.onload = () => {
            this.props.context.drawImage(this.state.hero, 0, 0, 256, 224, 0, 0, 256, 224)
        }

    }


    render() {
        return (
            <div id='hero'>
                {this.renderHero()}
            </div>
        )
    }
}
export default Hero;

