import React, { Component } from 'react';
import HeroSprite from './sprites/hero.png'

class Hero extends Component {
    constructor(){
        super();
        this.state = {
            spriteSheetCol: 6,
            spriteSheetHeight: 224,
            spriteSheetWidth: 256

        }
    }
    componentDidMount(){
        const hero = new Image
        hero.src = HeroSprite
        hero.onload = () => {
            this.props.context.drawImage(hero, 0, 0)
        }
    }
    render() {
        return(
            <div id='hero'>

            </div>
        )
    }
}
export default Hero;
