import React, { Component } from "react";
import GameHeader from "./GameHeader";
import GameShow from "./GameShow";
import GameFooter from "./GameFooter";
import UserBar from "./UserBar";

let baseURL = "http://localhost:3000/";
class App extends Component {
  constructor() {
    super();
    this.state = {
      level: [],
      monsters: [],
      users: [],
      activeUser: [],
      words: [],
      bossWords: [],
      activeWords: ["walter", "fish"],
    };
  }
  //initial setup for game data
  componentDidMount() {
    this.fetchLevel();
    this.fetchUsers();
    this.fetchMonsters()
  }
  //****FETCH FUNCTIONS THAT FOR SOME REASON I COULDN'T REFACTOR?? REACT IS DUMB****//
  fetchLevel = () => {
    return fetch(baseURL + "levels")
      .then((resp) => resp.json())
      .then((level) => this.setState({ level }));
  };
  fetchUsers = () => {
    return fetch(baseURL + "users")
      .then((resp) => resp.json())
      .then((users) => this.setState({ users }));
  };
  fetchMonsters = () => {
    return fetch(baseURL + "monsters")
      .then((resp) => resp.json())
      .then((monsters) => this.setState({ monsters }));
  };






  //***Helper functions *//

  //take user array and pull usernames into separate array for easy comparison
  // parseUsernames = () => {
  //   console.log(this.state.usernames)
  //   let usernames = []
  //   this.state.users.map(user => usernames.push(user))
  //   this.setState({usernames})
  // }

  //create user model in DB
  createUser = (userObject) => {
    fetch(baseURL + "users", userObject)
    .then(this.fetchUsers())
    .then(this.setState)
  }

  //given User object, check if input of username and password exists in database, if so set activeUser to username 
  UserLogin = (username, password) => {
    let currentUser = this.state.users.filter(user => user.Name === username)
    const index = this.state.usernames.indexOf(username);
    if (index > -1) {
      console.log(event.target.value);
      this.setState({ value: "" });
      event.target.value = "";
    }
  };

  checkIfLoggedIn = () => {
    if (this.state.activeUser.length > 0) {
      return <GameShow words={this.state.level} />;
    } else {
      // this.parseUsernames()
      return <UserBar parseUsernames={this.parseUsernames} logIn={this.UserLogin} createUser={this.createUser}/>;
    }
  };

  render() {
    return (
      <div className="App">
        <GameHeader />
        {/* <GameShow words={this.state.level}/> */}
        {this.checkIfLoggedIn()}
        <GameFooter words={this.state.activeWords} />
      </div>
    );
  }
}
export default App;
