import React, { Component } from "react";

class GameFooter extends Component {
  constructor(props) {
    super(props);
    this.state = { value: "", words: this.props.words };

  }

  checkValue = (event) => {
    const index = this.state.words.indexOf(event.target.value);
    if (index > -1) {
      console.log(event.target.value);
      this.setState(this.state.words.splice(index, 1));
      this.setState({ value: "" });
      event.target.value = "";
    }
  };

  handleChange = (event) => {
    console.log(event.target.value);
    this.setState({ value: event.target.value });
    this.checkValue(event);
  };

  render() {
    return (
      <div

      >
        <div
          style={{
          display: "flex",
          justifyContent: "center",
        }}
        >
          {" "}
          <h3 style={{color:'red'}}>{this.props.words.join(", ")}</h3>{" "}
        </div>
        <div
          style={{
          display: "flex",
          justifyContent: "center",
        }}
        >
          <form>
            <input type="text" name="name" onChange={this.handleChange} />
          </form>
        </div>
        <div
          style={{
          display: "flex",
          justifyContent: "center",
        }}
        >
          {" "}
          <h3>This text is secret</h3>{" "}
        </div>
      </div>
    );
  }
}
export default GameFooter;
