import React, { Component } from "react";

class UserBar extends Component {
  constructor(props) {
    super(props);
    this.state = { value: "", checked: false };
  }

  handleChecked () {
    this.setState({isChecked: !this.state.checked});
  }
//   existingUserLogin = () => {
//     this.props.parseUsernames();
//     return (

//     );
//   };

//   newUserSignup = () => {
//     return (

//     );
//   };

  render() {
      let inputBar;
      if (this.state.checked){
        inputBar = `<div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
          }}
        >
          <h1>Log in, friend!</h1>
        </div>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
          }}
        >
          <form>
            <label>Username</label>
            <input type="text" name="username" onChange={this.props.logIn} />

            <label>Password</label>
            <input
              type="password"
              name="password"
              onChange={this.props.logIn}
            />
          </form>
        </div>
      </div>`
      } else {
          inputBar = `<div>
          <div
            style={{
              display: "flex",
              justifyContent: "center",
            }}
          >
            <h1>Create an account!</h1>
          </div>

          <div
            style={{
              display: "flex",
              justifyContent: "center",
            }}
          >
            <form>
              <label>Username</label>
              <input type="text" name="username" />

              <label>Password</label>
              <input type="password" name="password" />
              <submit></submit>
            </form>
          </div>
        </div>`
      }
    return (
      <div>
        <div
          class="toggle-switch"
          style={{
            display: "flex",
            justifyContent: "center",
          }}
        >
          <input
            type="checkbox"
            class="toggle-switch-checkbox"
            name="toggleSwitch"
            id="toggleSwitch"
            defaultChecked={this.state.checked}
            onChange={
              this.handleChecked
            }
          />
          <label class="toggle-switch-label" for="toggleSwitch">
            I have an account!
          </label>
        </div>
        {inputBar}</div>

    );
  }
}
export default UserBar;
