import React, { Component } from 'react';
import HeroSprite from './sprites/hero.png'

class Hero extends Component {
    // constructor(){
    //     super();
    //     this.state = {
    //         spriteSheetCol: 6,
    //         spriteSheetHeight: 224,
    //         spriteSheetWidth: 256
    //     }
    // }
    componentDidMount() {
        const hero = new Image()
        hero.src = HeroSprite
    }


    renderHero = () => {
        hero.onload = () => {
            this.props.context.drawImage(hero, 0, 0, 256, 224, 0, 0, 256, 224)
        }

    }


    render() {
        return (
            <div id='hero'>
                {this.renderHero()}
            </div>
        )
    }
}
export default Hero;

