import React, { Component } from "react";

class UserBar extends Component {
  constructor(props) {
    super(props);
    this.state = { value: "", checked: false };
    this.handleChecked = this.handleChecked.bind(this);
  }

  handleChecked() {
    this.setState({ checked: !this.state.checked });
  }

  createUser = (e) => {
    e.preventDefault();
    console.log(e.target.username.value);
  };

  logInUser = (e) => {
    e.preventDefault();
    console.log(e.target);
  };
  render() {
    let inputBar;
    //conditional statement to verify if you have an account or not by checkbox, and adjust display accordingly
    //if checked, log in option
    if (this.state.checked) {
      //   this.props.parseUsernames();
      inputBar = (
        <div>
          <div
            style={{
              display: "flex",
              justifyContent: "center",
            }}
          >
            <h1>Log in, friend!</h1>
          </div>

          <div
            style={{
              display: "flex",
              justifyContent: "center",
            }}
          >
            <form onSubmit={this.props.logIn}>
              <label>Username</label>
              <input type="text" name="username" />
              <br></br>
              <label>Password</label>
              <input type="password" name="password" />
              <br></br>
              <button >submit</button>
            </form>
          </div>
        </div>
      );
    } else {
        //else create Account
      inputBar = (
        <div>
          <div
            style={{
              display: "flex",
              justifyContent: "center",
            }}
          >
            <h1>Create an account!</h1>
          </div>

          <div
            style={{
              display: "flex",
              justifyContent: "center",
            }}
          >
            <form onSubmit={this.createUser}>
              <label>Username</label>
              <input type="text" name="username" />
              <br></br>
              <label>Password</label>
              <input type="password" name="password" />
              <br></br>
              <button>submit</button>
            </form>
          </div>
        </div>
      );
    }
    return (
      <div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
          }}
        >
          <input type="checkbox" onChange={this.handleChecked} />
          <label>I have an account!</label>
        </div>
        {inputBar}
      </div>
    );
  }
}
export default UserBar;
