import React, { Component } from 'react';

class GameShow extends Component {
    constructor() {
        super();
        this.state = {
            canvasWidth: 500,
            canvasHeight: 400,
            context: null,
            tileSheetColumns: 8,
            matrixColumns: 19,
            tileRows: 10,
            tileHeight: 16,
            tileWidth: 16,
            world: []
        }
    }
    render() {
        return(
            <canvas id="gameCanvas" style={{
                width: `${this.state.canvasWidth}px`,
                height: `${this.state.canvasHeight}px`,
                paddingRight: '0',
                paddingLeft: '0',
                marginLeft: 'auto',
                marginRight: 'auto',
                display: 'block',
                border: "20px solid white",
                backgroundColor: 'black'
            }}
            >

            </canvas>
        )
    }
}
export default GameShow;
